function getAddressArr(addressArr: string[]) {
    let requestPromiseArr = addressArr.map((address) => {
        return $.ajax({
            url: 'https://restapi.amap.com/v3/geocode/geo',
            data: {
                key: 'de1fb1729e2e4f6fb20d2e6ea3ac8e27',
                address: address
            }
        })
    })
    return Promise.all(requestPromiseArr)
        .then((resultArr: any[]) => {
            return resultArr.map(result => result.geocodes[0].location)
        })
}

