let destAvgTime: Ref<any[]> = ref([])
let destAvgVariance: Ref<any[]> = ref([])

onMounted(async () => {
    let destCoords = await getAddressArr(destAddressArr.map(name => '南京市' + name + '地铁站'))
    let srcCoords = await getAddressArr(srcAddressArr.map(name => '南京市' + name))
    resultArrArr.value = await getRouteRequestArr(destCoords, srcCoords)
    destAvgTime.value = (() => {
        let destAvgTimeArr = []
        for (let i = 0; i < destAddressArr.length; i++) {
            let totalTime = resultArrArr.value.map(resultArr => resultArr[i].minute)
            destAvgTimeArr.push(Math.round(totalTime.reduce((acc, time) => acc + time, 0) / totalTime.length))
        }
        return destAvgTimeArr
    })()
    destAvgVariance.value = (() => {
        let destAvgTimeArr = []
        for (let i = 0; i < destAddressArr.length; i++) {
            let totalTime = resultArrArr.value.map(resultArr => resultArr[i].minute)
            destAvgTimeArr.push(getVariance(totalTime))
        }
        return destAvgTimeArr
    })()
})

function getVariance(totalTime: number[]) {
    const average = totalTime.reduce((sum, value) => sum + value, 0) / totalTime.length;
    const diffs = totalTime.map(value => value - average);
    const squaredDiffs = diffs.map(diff => diff ** 2);
    const variance = squaredDiffs.reduce((sum, value) => sum + value, 0) / totalTime.length;
    return Math.round(variance);
}

